mapper.select:


	SELECT *
	FROM (SELECT A.*,
	             @ROWNUM := @ROWNUM + 1 AS RN
	      FROM (SELECT *
	            FROM user
	            WHERE user_name = 'admin'
	            ORDER BY user_no DESC) A, (SELECT @ROWNUM := 0) D
 	)B
 	WHERE RN > (#{page}-1) * #{amount} AND RN <= #{page} * #{amount}
